import { clientsClaim } from 'workbox-core';
import { ExpirationPlugin } from 'workbox-expiration';
import { precacheAndRoute, createHandlerBoundToURL } from 'workbox-precaching';
import { registerRoute } from 'workbox-routing';
import { StaleWhileRevalidate, CacheFirst } from 'workbox-strategies';

clientsClaim();

// Precache all resources listed in the manifest
precacheAndRoute(self.__WB_MANIFEST);

// Regular expression to match file extensions
const fileExtensionRegexp = new RegExp('/[^/?]+\\.[^/]+$');

// Handle navigation requests
registerRoute(
  ({ request, url }) => {
    if (request.mode !== 'navigate') {
      return false;
    }
    if (url.pathname.startsWith('/_')) {
      return false;
    }
    if (url.pathname.match(fileExtensionRegexp)) {
      return false;
    }
    return true;
  },
  createHandlerBoundToURL(process.env.PUBLIC_URL + '/index.html')
);

// Cache images with a StaleWhileRevalidate strategy
registerRoute(
  ({ url }) => url.origin === self.location.origin && url.pathname.endsWith('.png'),
  new StaleWhileRevalidate({
    cacheName: 'images',
    plugins: [
      new ExpirationPlugin({ maxEntries: 50 }),
    ],
  })
);

// Cache static assets (js, css, svg, html) with a StaleWhileRevalidate strategy
registerRoute(
  ({ url }) => url.origin === self.location.origin && /\.(js|css|svg|html)$/.test(url.pathname),
  new StaleWhileRevalidate({
    cacheName: 'assets',
    plugins: [
      new ExpirationPlugin({ maxEntries: 100 }),
    ],
  })
);

// Optionally, cache API responses (if you have API endpoints)
registerRoute(
  ({ url }) => url.origin === 'https://api.yourdomain.com', // Replace with your API origin
  new StaleWhileRevalidate({
    cacheName: 'api-cache',
    plugins: [
      new ExpirationPlugin({ maxEntries: 50 }),
    ],
  })
);

// Handle skip waiting for updates
self.addEventListener('message', (event) => {
  if (event.data && event.data.type === 'SKIP_WAITING') {
    self.skipWaiting();
  }
});
